mapper.select:
select * from (select rownum RNUM, NUM, CATEGORY_NUM as CATEGORYNUM, MENU, PRICE, REGDATE, USING from (select * from MENU
where MENU like '%' || #{menu} ||'%'
order by NUM asc ,REGDATE asc)) A, (select * from CATEGORY
where TYPE like '%' || #{type} ||'%'
) B where B.NUM = A.CATEGORYNUM and A.RNUM >= #{start} and A.RNUM <= #{end}

select * from (select rownum RNUM, NUM as categoryNum, TYPE, CATEGORY from (select * from CATEGORY
where CATEGORY like '%' || #{category} || '%'
order by NUM asc)) where RNUM >= #{start} and RNUM <= #{end}
